package com.wubin.test.encrypt;

import cn.hutool.core.io.FileUtil;
import org.springframework.util.Base64Utils;
import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

import java.io.File;
import java.io.IOException;
import java.util.Base64;

public class TestBase64 {

    public static void toBase64(byte[] bytes) {
        // 会换行
        BASE64Encoder encoder = new BASE64Encoder();
        System.out.println(encoder.encode(bytes));

        // 不会换行
        System.out.println(Base64.getEncoder().encodeToString(bytes));

        // 不会换行
        System.out.println(Base64Utils.encodeToString(bytes));

        // 不会换行
        System.out.println(cn.hutool.core.codec.Base64.encode(bytes));
    }

    public static void toStr(String base64) throws IOException {
        BASE64Decoder decoder = new BASE64Decoder();
        System.out.println(new String(decoder.decodeBuffer(base64)));
        System.out.println(new String(Base64.getDecoder().decode(base64)));
        System.out.println(new String(Base64Utils.decodeFromString(base64)));
        System.out.println(cn.hutool.core.codec.Base64.decodeStr(base64));
    }

    public static void toFile(String base64) throws IOException {
        BASE64Decoder decoder = new BASE64Decoder();
        FileUtil.writeBytes(decoder.decodeBuffer(base64), "D:\\test\\test1.jpg");
        FileUtil.writeBytes(Base64.getDecoder().decode(base64), "D:\\test\\test2.jpg");
        FileUtil.writeBytes(Base64Utils.decodeFromString(base64), "D:\\test\\test3.jpg");
        cn.hutool.core.codec.Base64.decodeToFile(base64, new File("D:\\test\\test4.jpg"));
    }

    public static void main(String[] args) throws IOException {
//        byte[] bytes = "你好".getBytes();
//        toBase64(bytes);
//        byte[] bytes = FileUtil.readBytes("D:\\test\\test.jpg");
//        toBase64(bytes);

//        toStr("5L2g5aW9");

        toFile("");
    }

}
